mapper.select:
SELECT 
  IFNULL(SUM(amount),0)
FROM
  journalizing_info 
WHERE item_id = #{id}
  AND company_id = #{companyId}
  AND TYPE = #{type} 
  AND data_source = #{datasource} 
  AND occurrence_time BETWEEN #{startDate}
  AND #{endDate}

SELECT 
  IFNULL(SUM(amount),0) 
FROM
  journalizing_info 
WHERE (item_id=#{id} or item_id in (SELECT child_id FROM doc_item_level WHERE parent_id=#{id}))
  AND company_id = #{companyId}
  AND TYPE = #{type} 
  AND data_source = #{datasource} 
  AND occurrence_time BETWEEN #{startDate}
  AND #{endDate}
