mapper.select:
SELECT 
  IFNULL(SUM(amount),0)
FROM
  journalizing_info 
WHERE item_id = #{id}
  AND company_id = #{companyId}
  AND TYPE = #{type} 
  AND data_source = #{datasource} 
  AND occurrence_time BETWEEN #{startDate}
  AND #{endDate}

SELECT 
  IFNULL(SUM(amount),0) 
FROM
  journalizing_info 
WHERE item_code LIKE (SELECT item_code FROM journalizing_info WHERE item_id=#{id} LIMIT 1)||'%' 
  AND company_id = #{companyId}
  AND TYPE = #{type} 
  AND data_source = #{datasource} 
  AND occurrence_time BETWEEN #{startDate}
  AND #{endDate}
